import React, { Component } from "react";
import { connect } from "react-redux";

let styles = {
  deleteBtnStyle: {
    cursor: "pointer"
  }
};

class Dashboard extends Component {
  renderExpenseTable() {
    let { expenseList } = this.props;
    const { deleteBtnStyle } = styles;
    console.log("[Dashboard Render]", expenseList);

    return expenseList.map(expense => {
      if (expense.deleted) {
        return (
          <tr>
            <td>{expense.category}</td>
            <td>{expense.itemName}</td>
            <td>{expense.amount}</td>
            <td>{expense.expenseDate}</td>
            <td>
              <i class="fas fa-undo" style={deleteBtnStyle} />
            </td>
          </tr>
        );
      }

      return (
        <tr>
          <td>{expense.category}</td>
          <td>{expense.itemName}</td>
          <td>{expense.amount}</td>
          <td>{expense.expenseDate}</td>
          <td>
            <i class="fas fa-pen" /> | <i class="fas fa-trash-alt" /> |
            <i class="fas fa-undo" />
          </td>
        </tr>
      );
    });
  }

  render() {
    // let tdStyle = { boxShadow: "0 .125rem .25rem rgba(0,0,0,.075) !important" };

    return (
      <table className="table">
        <thead>
          <tr>
            <th>Category</th>
            <th>Item name</th>
            <th>Amount</th>
            <th>Expense date</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>{this.renderExpenseTable()}</tbody>
      </table>
    );
  }
}

const mapStateToProps = state => {
  console.log("[State]", state);
  return state;
};
export default connect(mapStateToProps)(Dashboard);
