import React, { Component } from "react";
import { connect } from "react-redux";
import Modal from "react-bootstrap/Modal";
import DoughnutChart from "./chartDonught";

let styles = {
  deleteBtnStyle: {
    cursor: "pointer"
  },
  editBtnStyle: {
    cursor: "pointer"
  },
  undoBtnStyle: {
    cursor: "pointer"
  },
  addExpBtnStyle: {
    border: "border: 1px solid #eee",
    boxShadow: " 0 2px 3px #ccc",
    borderRadius: "0"
  }
};

class Dashboard extends Component {
  state = {
    show: false
  };

  handleClose = () => {
    this.setState({ show: false });
  };

  handleShow = () => {
    this.setState({ show: true });
  };

  renderExpenseTable() {
    let { expenseList } = this.props;
    const { deleteBtnStyle, undoBtnStyle, editBtnStyle } = styles;
    console.log("[Dashboard Render]", expenseList);

    return expenseList.map(expense => {
      if (expense.deleted) {
        return (
          <tr className="strikeout">
            <td>{expense.category}</td>
            <td>{expense.itemName}</td>
            <td>{expense.amount}</td>
            <td>{expense.expenseDate}</td>
            <td>
              <i
                class="fas fa-undo"
                title="Undo changes"
                style={undoBtnStyle}
              />
            </td>
          </tr>
        );
      }

      return (
        <tr>
          <td>{expense.category}</td>
          <td>{expense.itemName}</td>
          <td>{expense.amount}</td>
          <td>{expense.expenseDate}</td>
          <td>
            <i class="fas fa-pen" style={editBtnStyle} title="Edit" /> |{" "}
            <i class="fas fa-trash-alt" title="Delete" style={deleteBtnStyle} />
          </td>
        </tr>
      );
    });
  }

  render() {
    // let tdStyle = { boxShadow: "0 .125rem .25rem rgba(0,0,0,.075) !important" };
    const { addExpBtnStyle } = styles;
    return (
      <div className="container">
        <div class="row mt-2">
          <div class="col-md-6">
            <div className="card">
              <div className="card-header">Budget Overview</div>

              <div
                className="card-body"
                style={{ marginLeft: "0", paddingLeft: "0" }}
              >
                <div className="row">
                  <div className="col-xs-12 col-sm-8 col-md-8 col-lg-8">
                    <DoughnutChart label="25% spent" />
                  </div>
                  <div className=" col-xs-12 col-sm-4 col-md-4 col-lg-4">
                    <p>Total budget</p>
                    <p>
                      <strong>Rs</strong>2000000
                    </p>
                    <p>Total Expense</p>
                    <p>
                      <strong>Rs</strong>12000000
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div class="col-md-6">
            <div class="card">
              <div className="card-header">Category wise split</div>
              <h5 class="card-body">Special title treatment</h5>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-md-3">
            <button
              type="button"
              style={addExpBtnStyle}
              class="btn "
              onClick={this.handleShow}
            >
              Add Expense
            </button>
          </div>
        </div>
        <div className="row customeCard">
          {/* table */}
          <table className="table">
            <thead>
              <tr>
                <th>Category</th>
                <th>Item name</th>
                <th>Amount</th>
                <th>Expense date</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>{this.renderExpenseTable()}</tbody>
          </table>
        </div>

        {/*------model start only last div is not ihn model
----*/}

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <button
              type="button"
              class="btn btn-primary"
              onClick={this.handleClose}
            >
              Close
            </button>
            <button
              type="button"
              class="btn btn-primary"
              onClick={this.handleClose}
            >
              Save Changes
            </button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log("[State]", state);
  return state;
};
export default connect(mapStateToProps)(Dashboard);
