import React, { Component } from "react";
import { connect } from "react-redux";
import { login, userIdOnChnage, passwordOnChnage } from "../actions";
class Login extends Component {
  state = {};

  renderLoginButton() {
    if (this.props.loginBtnLoader) {
      //------------
      return (
        <p className="buttonStyle" style={{ padding: "8px" }}>
          Authenicating...
        </p>
      );
      //-------------
    } else {
      <button value="login" className="buttonStyle">
        Login
      </button>;
    }
  }

  renderCreateAccountButton() {
    //------------
    return (
      <button value="createAccount" className="buttonStyle">
        Areate Account
      </button>
    );
    //-------------
  }

  componentDidMount() {
    console.log("Login did mount  this.props", this.props);
  }
  render() {
    return (
      <div className="row mt-5 text-center">
        <div className="col-xm-12 col-sm-4 col-md-4 col-lg-4" />
        <div className="col-xm-12 col-sm-4 col-md-4 col-lg-4 card p-5 m-2">
          <input
            type="text"
            name="userId"
            value={this.props.userId}
            placeholder="user@gmail.com"
            onChange={e => {
              this.props.userIdOnChnage(e.target.value);
            }}
            className="textBoxStyle"
          />

          <input
            type="password"
            name="password"
            placeholder="Enter your password"
            value={this.props.password}
            onChange={e => {
              this.props.passwordOnChnage(e.target.value);
            }}
            className="textBoxStyle"
          />

          <br />
          <p className="text-center" style={{ color: "red" }}>
            {" "}
            Sorry user can not login{" "}
          </p>
          {/*  <p className="text-center" style="color:green"> Sorry user can not login </p> */}
          {this.renderLoginButton()}
          {/*---*/}

          {this.renderCreateAccountButton()}

          <br />
        </div>
        <div className="col-xm-12 col-sm-4 col-md-4 col-lg-4" />
      </div>
    );
  }
}
const mapStateToProps = state => {
  console.log("[State Login component]", state.authentication);
  return state.authentication;
};
export default connect(
  mapStateToProps,
  { login, userIdOnChnage, passwordOnChnage }
)(Login);
