import React, { Component } from "react";
import { connect } from "react-redux";
import Modal from "react-bootstrap/Modal";
import DoughnutChart from "./chartDonught";
import { Redirect } from "react-router-dom";
import { loginRoute } from "../config/route";

let styles = {
  deleteBtnStyle: {
    cursor: "pointer"
  },
  editBtnStyle: {
    cursor: "pointer"
  },
  undoBtnStyle: {
    cursor: "pointer"
  },
  addExpBtnStyle: {
    border: "border: 1px solid #eee",
    boxShadow: " 0 2px 3px #ccc",
    borderRadius: "0"
  }
};

class Dashboard extends Component {
  state = {
    show: false
  };

  componentDidMount() {
    console.log("[DASHBOARD] comDidMnt: this.props", this.props);
  } //authentication
  redirectToLoginIfNotLogin(isLogin) {
    console.log("redirectToLoginIfNotLogin ", !isLogin);
    if (!isLogin) {
      //  this.props.initializeApp();

      console.log("[RedirectTo] Login page");
      return <Redirect to={loginRoute} />;
    }
  }

  handleClose = () => {
    this.setState({ show: false });
  };

  handleShow = () => {
    this.setState({ show: true });
  };

  renderExpenseTable() {
    let { expenseList } = this.props;
    const { deleteBtnStyle, undoBtnStyle, editBtnStyle } = styles;
    console.log("[Dashboard Render]", expenseList);

    return expenseList.map(expense => {
      if (expense.deleted) {
        return (
          <tr className="strikeout" key={expense.id}>
            <td>{expense.category}</td>
            <td>{expense.itemName}</td>
            <td>{expense.amount}</td>
            <td>{expense.expenseDate}</td>
            <td>
              <i
                className="fas fa-undo"
                title="Undo changes"
                style={undoBtnStyle}
              />
            </td>
          </tr>
        );
      }

      return (
        <tr key={expense.id}>
          <td>{expense.category}</td>
          <td>{expense.itemName}</td>
          <td>{expense.amount}</td>
          <td>{expense.expenseDate}</td>
          <td>
            <i className="fas fa-pen" style={editBtnStyle} title="Edit" /> |{" "}
            <i
              className="fas fa-trash-alt"
              title="Delete"
              style={deleteBtnStyle}
            />
          </td>
        </tr>
      );
    });
  }

  renderaddBody = () => {
    return (
      <div className="row mt-5 text-center">
        <div className="col-xm-12 col-sm-4 col-md-4 col-lg-4" />
        <div className="col-xm-12 col-sm-4 col-md-4 col-lg-4 card p-5 m-2">
          {this.redirectToDasboard()}
          <input
            type="text"
            name="userId"
            value={"hello"}
            placeholder="user@gmail.com"
            onChange={e => {
              //this.props.userIdOnChnage(e.target.value);
              alert("Ok");
            }}
            className="textBoxStyle"
          />

          <input
            type="password"
            name="password"
            placeholder="Enter your password"
            value={this.props.password}
            onChange={e => {
              this.props.passwordOnChnage(e.target.value);
            }}
            className="textBoxStyle"
          />

          <br />
          <p className="text-center" style={{ color: "red" }}>
            {" "}
            {this.props.error}
          </p>
          <p className="text-center" style={{ color: "green" }}>
            {" "}
            {this.props.message}{" "}
          </p>
          {this.renderLoginButton()}
          {/*---*/}

          {this.renderCreateAccountButton()}

          <br />
        </div>
        <div className="col-xm-12 col-sm-4 col-md-4 col-lg-4" />
      </div>
    );
  };

  renderExpensePopup = props => {
    return (
      <Modal
        show={this.state.show}
        style={{ border: "1px soild red" }}
        onHide={this.handleClose}
      >
        <Modal.Header closeButton>
          <Modal.Title>Add Expense</Modal.Title>
        </Modal.Header>
        <Modal.Body>{this.renderaddBody()}</Modal.Body>
        <Modal.Footer>
          <button
            type="button"
            className="btn btn-primary"
            onClick={this.handleClose}
          >
            Close
          </button>
          <button
            type="button"
            className="btn btn-primary"
            onClick={this.handleClose}
          >
            Save Changes
          </button>
        </Modal.Footer>
      </Modal>
    );
  };

  render() {
    // let tdStyle = { boxShadow: "0 .125rem .25rem rgba(0,0,0,.075) !important" };

    const { addExpBtnStyle } = styles;
    return (
      <div className="container">
        {this.redirectToLoginIfNotLogin(
          this.props.authentication.isSessionActive
        )}
        <div className="row mt-2">
          <div className="col-md-6">
            <div className="card">
              <div className="card-header">Budget Overview</div>

              <div
                className="card-body"
                style={{ marginLeft: "0", paddingLeft: "0" }}
              >
                <div className="row">
                  <div className="col-xs-12 col-sm-8 col-md-8 col-lg-8">
                    <DoughnutChart label="25% spent" />
                  </div>
                  <div className=" col-xs-12 col-sm-4 col-md-4 col-lg-4">
                    <p>Total budget</p>
                    <p>
                      <strong>Rs</strong>2000000
                    </p>
                    <p>Total Expense</p>
                    <p>
                      <strong>Rs</strong>12000000
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="col-md-6">
            <div className="card">
              <div className="card-header">Category wise split</div>
              <h5 className="card-body">Special title treatment</h5>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-md-3">
            <button
              type="button"
              style={addExpBtnStyle}
              className="btn "
              onClick={this.handleShow}
            >
              Add Expense
            </button>
          </div>
        </div>
        <div className="row customeCard">
          {/* table */}
          <table className="table">
            <thead>
              <tr>
                <th>Category</th>
                <th>Item name</th>
                <th>Amount</th>
                <th>Expense date</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>{this.renderExpenseTable()}</tbody>
          </table>
        </div>
        {/* popup for expense */}
        {this.renderExpensePopup(this.props)}
        {/* pop ende here..*/}
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log("[State]", state);
  return state;
};
export default connect(mapStateToProps)(Dashboard);
