import {
  BUDGET_CHANGE,
  CATEGORY_CHANGE,
  UPDATE_BUDGET_FAILED,
  UPDATE_BUDGET_SUCCESS,
  UPDATING_BUDGET,
  ADD_CATEGORY,
  ADDING_CATEGORY,
  ADD_CATEGORY_SUCCESS,
  ADD_CATEGORY_FAILED
} from "../config/actionType";

const INITIAL_STATE = {
  budget: 0,
  categories: [],
  category: "",
  loadingUpdate: false,

  error: " "
};
export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case BUDGET_CHANGE:
      //  console.log("[Action]BudgetChnage", BUDGET_CHANGE, "state", state);
      return { ...state, budget: action.payload };

    case CATEGORY_CHANGE:
      console.log("[Category]");
      return { ...state, category: action.payload };
    // start categoty
    case ADD_CATEGORY:
      console.log("[Action]: addCategory");
      return { ...state, categories: action.payload };

    case ADDING_CATEGORY:
      console.log("[Action]: addingCategory");
      return { ...state, loadingUpdate: true, error: "Adding..." };
    // end category
    case UPDATING_BUDGET:
      console.log("[Action]: updating udget");
      return { ...state, loadingUpdate: true, error: "" };

    case UPDATE_BUDGET_SUCCESS:
      console.log("[Action]: updateBudget");
      return {
        ...state,
        budget: action.payload,
        error: "",
        loadingUpdate: false
      };
    case UPDATE_BUDGET_FAILED:
      console.log("[Action]: updateBudget");
      return {
        ...state,
        budget: 0,
        error: action.payload,
        loadingUpdate: false
      };
    default:
      return state;
      break;
  }
};
