import Axious from "axios";
import { UpdateBudgetRoute } from "../config/route";
import {
  BUDGET_CHANGE,
  CATEGORY_CHANGE,
  ADD_CATEGORY,
  UPDATE_BUDGET
} from "../config/actionType";

export const budgetOnChange = budget => {
  return {
    type: BUDGET_CHANGE,
    payload: budget
  };
};

export const categoryOnChange = category => {
  return {
    type: CATEGORY_CHANGE,
    payload: category
  };
};

export const addCategory = category => {
  // make async  http call and save it to database

  return dispatch => {
    // do call here and wait for response
  };

  return {
    type: ADD_CATEGORY,
    payload: category
  };
};

export const updateBudget = budget => {
  console.log("[Budget updated thanks]");

  return dispatch => {
    // do call here and wait for response
    Axious.post(UpdateBudgetRoute, {
      firstName: "Fred",
      lastName: "Flintstone"
    })
      .then(function(response) {
        console.log(response);
      })
      .catch(function(error) {
        console.log(error);
      });
  };
  return {
    type: UPDATE_BUDGET,
    payload: budget
  };
};
