import React, { Component } from "react";
import { connect } from "react-redux";
import {
  budgetOnChange,
  categoryOnChange,
  addCategory,
  updateBudget //loadingCategory
} from "../actions";
class Settings extends Component {
  renderAddCategoryBtn(btnStyle) {
    if (this.props.loadingCategory) {
      return <p style={{ display: "inline-block" }}>Adding...</p>;
    } else {
      return (
        <button
          type="button"
          onClick={e => {
            this.props.addCategory(this.props.category);
          }}
          style={btnStyle}
        >
          Add
        </button>
      );
    }
  }

  renderUpdateButton(btnStyle) {
    if (this.props.loadingUpdate) {
      return <p style={{ display: "inline-block" }}>Updating...</p>;
    } else {
      return (
        <button
          type="button"
          style={btnStyle}
          onClick={e => {
            this.props.updateBudget(this.props.budget);
          }}
        >
          Update
        </button>
      );
    }
  }

  render() {
    let rowStyle = {
      background: "white"
    };
    let btnStyle = {
      border: "border: 1px solid #eee",
      boxShadow: " 0 2px 3px #ccc",
      borderRadius: "0",
      width: "100px",
      display: "inline-box",
      background: "white",
      height: "35px"
    };
    let deleteBtnStyle = {
      cursor: "pointer",
      float: "right",
      padding: "5px",
      paddingRight: "20%"
    };
    return (
      <div className="container ">
        <div className="row mt-5" style={rowStyle}>
          <div className="col-md-3" />

          <div className="col-md-9 text-center">
            <label>
              Total budget
              <input
                type="text"
                name="totalBudget"
                style={{
                  display: "inline-box",
                  marginRight: "16px",
                  marginLeft: "16px"
                }}
                id="totalBudget"
                placeholder="Enter your  budget"
                value={this.props.budget}
                onChange={e => {
                  this.props.budgetOnChange(e.target.value);
                }}
              />
            </label>
            {this.renderUpdateButton(btnStyle)}
          </div>
          <div className="col-md-2" />
        </div>
        <br />
        <div className="row mt-5" style={rowStyle}>
          <div className="col-md-3" />

          <div className="col-md-9 text-center">
            <label>
              Categories
              <input
                type="text"
                name="totalBudget"
                style={{
                  display: "inline-box",
                  marginRight: "16px",
                  marginLeft: "16px"
                }}
                value={this.props.category}
                placeholder="Enter category name"
                onChange={e => {
                  this.props.categoryOnChange(e.target.value);
                }}
              />
            </label>
            {this.renderAddCategoryBtn(btnStyle)}
          </div>
          <div className="col-md-2" />
        </div>
        <br /> <br />
        <div
          className="row"
          style={{
            borderRadius: "5px",
            borderColor: "black"
          }}
        >
          <div className="col-md-12">
            <p className="text-center" style={{ color: "red" }}>
              {this.props.error}
            </p>
          </div>
        </div>
        <div className="row mt-6" style={rowStyle}>
          <div className="col-md-4" />

          <div className="col-md-3 mt-5  card">
            <ul>
              <li className="categorie">
                Item1{" "}
                <span
                  className="fas fa-trash-alt"
                  title="Delete"
                  style={deleteBtnStyle}
                />
              </li>
              <li className="categorie">Item2</li>
              <li className="categorie">Item3</li>
              <li className="categorie">Item4</li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}
const mapStateToProps = state => {
  console.log("[Settings Comp]-", state);
  return state.settings;
};

export default connect(
  mapStateToProps,
  { budgetOnChange, categoryOnChange, updateBudget, addCategory }
)(Settings);
