  };

  return {
    type: ADD_CATEGORY,
    payload: category
  };
};

export const updateBudget = budget => {
  console.log("[Budget updated thanks]");

  return dispatch => {
    // do call here and wait for response
    Axious.post(UpdateBudgetRoute, {
      userId: "sudheergupta@gmnail.com",
      budget: budget
    })
      .then(response => {
        console.log("Server Respodsfwsnse", JSON.parse(response));
        dispatch({
          type: UPDATE_BUDGET,
          payload: budget
        });
      })
      .catch(function(error) {
        dispatch({
          type: UPDATE_BUDGET,
          payload: budget
        });

        console.log("Server Error", error);
      });
  };
};
