import React, { Component } from "react";
import { connect } from "react-redux";

let styles = {
  deleteBtnStyle: {
    cursor: "pointer"
  },
  editBtnStyle: {
    cursor: "pointer"
  },
  undoBtnStyle: {
    cursor: "pointer"
  }
};

class Dashboard extends Component {
  renderExpenseTable() {
    let { expenseList } = this.props;
    const { deleteBtnStyle, undoBtnStyle, editBtnStyle } = styles;
    console.log("[Dashboard Render]", expenseList);

    return expenseList.map(expense => {
      if (expense.deleted) {
        return (
          <tr className="strikeout">
            <td>{expense.category}</td>
            <td>{expense.itemName}</td>
            <td>{expense.amount}</td>
            <td>{expense.expenseDate}</td>
            <td>
              <i
                class="fas fa-undo"
                title="Undo changes"
                style={undoBtnStyle}
              />
            </td>
          </tr>
        );
      }

      return (
        <tr>
          <td>{expense.category}</td>
          <td>{expense.itemName}</td>
          <td>{expense.amount}</td>
          <td>{expense.expenseDate}</td>
          <td>
            <i class="fas fa-pen" style={editBtnStyle} title="Edit" /> |{" "}
            <i class="fas fa-trash-alt" title="Delete" style={deleteBtnStyle} />
          </td>
        </tr>
      );
    });
  }

  render() {
    // let tdStyle = { boxShadow: "0 .125rem .25rem rgba(0,0,0,.075) !important" };

    return (
      <div className="container">
        <div className="row">
          {/* 6*6 for charts  */}
          <div className="col-md-6">chart 1</div>

          <div className="col-md-6">chart 2</div>
        </div>

        <div className="row">
          {/* table */}
          <table className="table">
            <thead>
              <tr>
                <th>Category</th>
                <th>Item name</th>
                <th>Amount</th>
                <th>Expense date</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>{this.renderExpenseTable()}</tbody>
          </table>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log("[State]", state);
  return state;
};
export default connect(mapStateToProps)(Dashboard);
