import {
  BUDGET_CHANGE,
  CATEGORY_CHANGE,
  ADD_CATEGORY,
  UPDATE_BUDGET_FAILED,
  UPDATE_BUDGET_SUCCESS
} from "../config/actionType";

const INITIAL_STATE = {
  budget: 0,
  categories: [],
  category: "",
  loadingUpdate: false,

  error: " "
};
export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case BUDGET_CHANGE:
      //  console.log("[Action]BudgetChnage", BUDGET_CHANGE, "state", state);
      return { ...state, budget: action.payload };

    case CATEGORY_CHANGE:
      console.log("[Category]");
      return { ...state, category: action.payload };

    case ADD_CATEGORY:
      console.log("[Action]: addCategory");
      return { ...state, categories: action.payload };

    case UPDATE_BUDGET_SUCCESS:
      console.log("[Action]: updateBudget");
      return { ...state, budget: action.payload, error: "" };
    case UPDATE_BUDGET_FAILED:
      console.log("[Action]: updateBudget");
      return { ...state, budget: 0, error: "Budget failed while updating" };
    default:
      return state;
      break;
  }
};
